import logo from './logo.svg';
import './App.css';
import {Heading, Icon} from 'react-bulma-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLiraSign, faSlidersH } from '@fortawesome/free-solid-svg-icons';
import {SortableContainer, SortableElement} from 'react-sortable-hoc';
import {arrayMoveMutable} from 'array-move';
import { useState } from 'react';

import 'bulma/css/bulma.min.css';

function Header(props) {
  const style = {
    paddingRight: "30px"
  };

  return (
    <div class="header">          
      <h1 class="title">
        <span class="icon-text">
          <span class="icon has-text-info">
            <FontAwesomeIcon icon={faSlidersH} size="lg"/>
          </span>
          <span style={{"paddingLeft": "10px"}}>Turbo Stacks</span>
        </span>
      </h1>
    </div>
    );
}

const DraggableList = SortableContainer(({items}) => {
  return (
    <ul>
      {items.map((value, index) => (
        <DraggableListItem key={`item-${value}`} index={index} value={value} />
      ))}
    </ul>
  );
});
const DraggableListItem = SortableElement(({value}) => <li>{value}</li>);

function ItemCard(props) {
  const [items, setItems] = useState(0);

  const onSortEnd = ({oldIndex, newIndex}) => {
    setItems(arrayMoveMutable(items, oldIndex, newIndex));
  };

  return (
      <div style={{paddingTop:"100px",marginLeft: "200px", marginRight: "200px"}}>
        <div class="columns">
          <div class="column is-2">
            <h6 class="title is-6 bird" style={{paddingTop: "35px"}}>Chores To Do</h6>
          </div>
          <div class="column">
          <div class="card">
            <div class="card-content">
              <div>
                <div class="columns">
                  <div class="column is-11 blur">
                    <div class="main-text">
                    <ul id="items">
                      <li class="ui-state-default" id="list-hover"><span class="ui-icon ui-icon-arrowthick-2-n-s"></span>Item 1</li>
                    </ul>
                      </div>
                <div class="list-all">
                  <DraggableList items={items} onSortEnd={onSortEnd} />
                </div>
                    </div>
                    <div class="column is-1">
                      <span class="icon show-options">
                    <i class="fas fa-home"></i>
                  </span>
                    </div>
                </div>
            </div>
            </div>
            <footer class="card-footer">
                <a href="#" class="card-footer-item" id="see-all">See All</a>
                <a href="#" class="card-footer-item">Show All</a>
                <a href="#" class="card-footer-item">Keep Unblurred</a>
                <a href="#" class="card-footer-item">Delete</a>
            </footer>
          </div>
          </div>
        </div>
      </div>
    );
}

function App() {
  return (
    <div className="App">
      <section class="section">
        <div class="container">
          <Header/>
          <ItemCard/>
        </div>
      </section>
    </div>
  );
}

export default App;
