import logo from './logo.svg';
import './App.css';
import {Heading, Icon} from 'react-bulma-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGripHorizontal } from '@fortawesome/free-solid-svg-icons';
import {SortableContainer, SortableElement} from 'react-sortable-hoc';
import {arrayMoveImmutable} from 'array-move';
import { useState, useEffect} from 'react';
import React from 'react';
import Typed from "typed.js";

import 'bulma/css/bulma.min.css';

function Header(props) {
  const style = {
    paddingRight: "30px"
  };

  return (
    <div class="header">          
      <h1 class="title">
        <span class="icon-text">
          <span class="icon has-text-info">
            <FontAwesomeIcon icon={faGripHorizontal} size="lg"/>
          </span>
          <span style={{"paddingLeft": "10px"}}>Turbo Stacks</span>
        </span>
      </h1>
    </div>
    );
}

class TimeForm extends React.Component {
  constructor(props) {
    super(props);

    this.state = {value: 0};
    this.updateTimeAvailable = this.props.updateTimeAvailable;
  }

  componentDidMount() {
    this.setupAnimatedPrompt();
  }

  setupAnimatedPrompt() {
    const options = {
      strings: ["How much time do you have right now?"],
      typeSpeed: 25,
      startDelay: 5,
      showCursor: true
    };
    this.typed = new Typed(this.el, options);
  }

  handleChange = (event) => {
    this.setState({value: event.target.value});
  }

  handleSubmit = (event) => {
    console.log(this.state.value);
    this.updateTimeAvailable(this.state.value);
    event.preventDefault();
  }

  render() {
    return (
    <div className="columns is-centered">
      <div class="column is-7">
        <span className="title is-2" ref={(el) => {this.el=el;}} style={{whitespace: "pre"}}></span>
      </div>
      <div class="column is-2" style={{paddingTop: "20px"}}>
        <form onSubmit={this.handleSubmit}>
          <input class="input" type="number" placeholder="(minutes)" value={this.state.value} onChange={this.handleChange}/>
        </form>
      </div>
    </div>
    );
  }
}

class KnapsackResult extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
    <div className="columns is-centered">
      {this.props.timeAvailable}
    </div>
    );
  }
}

class Knapsack extends React.Component{
  constructor(props) {
    super(props);

    this.state = {
      showForm: true,
      timeAvailable: 0
    }
  }

  getStyles() {
    return {
      paddingTop: "150px"
    }
  }

  updateTimeAvailable = (newTimeAvailableMinutes) => {
    this.setState({timeAvailable: newTimeAvailableMinutes});
    this.setState({showForm: false});

    console.log(newTimeAvailableMinutes);
  }

  render() {
    return (
      <div style={this.getStyles()}>
      { this.state.showForm ?
        <TimeForm updateTimeAvailable={this.updateTimeAvailable}/> :
        <KnapsackResult timeAvailable={this.state.timeAvailable}/>
      }
      </div>
      );
  }
}

function App() {
  return (
    <div className="App">
      <section class="section">
        <div class="container">
          <Knapsack/>
        </div>
      </section>
    </div>
  );
}

export default App;
