import logo from './logo.svg';
import './App.css';
import {Heading, Icon} from 'react-bulma-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEllipsisV, faSlidersH } from '@fortawesome/free-solid-svg-icons';
import {SortableContainer, SortableElement} from 'react-sortable-hoc';
import {arrayMoveImmutable} from 'array-move';
import { useState, useEffect} from 'react';
import React from 'react';
import Typed from "typed.js";

import 'bulma/css/bulma.min.css';

function Header(props) {
  const style = {
    paddingRight: "30px"
  };

  return (
    <div class="header">          
      <h1 class="title">
        <span class="icon-text">
          <span class="icon has-text-info">
            <FontAwesomeIcon icon={faSlidersH} size="lg"/>
          </span>
          <span style={{"paddingLeft": "10px"}}>Turbo Stacks</span>
        </span>
      </h1>
    </div>
    );
}

class Knapsack extends React.Component{
  constructor(props) {
    super(props);
  }

  componentDidMount() {
    this.setupAnimatedPrompt();
  }

  setupAnimatedPrompt() {
    const options = {
      strings: ["How much time do you have right now?"],
      typeSpeed: 30
    };
    this.typed = new Typed(this.el, options);
  }

  getStyles() {
    return {
      paddingTop: "150px"
    }
  }

  render() {
    return (
    <div style={this.getStyles()} className="columns is-centered">
      <div class="column is-8">
        <span className="title is-2" ref={(el) => {this.el=el;}} style={{whitespace: "pre"}}></span>
      </div>
    </div>
    );
  }
}

function App() {
  return (
    <div className="App">
      <section class="section">
        <div class="container">
          <Header/>
          <Knapsack/>
        </div>
      </section>
    </div>
  );
}

export default App;
